import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as l,c as r,b as e,d as s,e as n,f as c}from"./app-4ed637ae.js";const i={},p={href:"https://www.zhihu.com/column/paogenjiudi",target:"_blank",rel:"noopener noreferrer"},h=e("h2",{id:"_1-概述",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-概述","aria-hidden":"true"},"#"),s(" 1. 概述")],-1),d=e("p",null,"Unicode字符集的目标是涵盖目前人类使用的所有字符，并为每个字符分配一个唯一的字符编号（即码点编号、码点值），一一对应于编号空间（Code Space，即代码空间、码空间、码点空间）里的码点（Code Point，即代码点）。",-1),m=e("p",null,[s("目前，Unicode字符集将所有字符按照使用上的频繁度，被划分为了17个平面（Plane），每个平面上的编号空间有 "),e("span",{class:"katex"},[e("span",{class:"katex-mathml"},[e("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[e("semantics",null,[e("mrow",null,[e("msup",null,[e("mn",null,"2"),e("mn",null,"16")]),e("mo",null,"="),e("mn",null,"65536")]),e("annotation",{encoding:"application/x-tex"},"2^{16}=65536")])])]),e("span",{class:"katex-html","aria-hidden":"true"},[e("span",{class:"base"},[e("span",{class:"strut",style:{height:"0.8141em"}}),e("span",{class:"mord"},[e("span",{class:"mord"},"2"),e("span",{class:"msupsub"},[e("span",{class:"vlist-t"},[e("span",{class:"vlist-r"},[e("span",{class:"vlist",style:{height:"0.8141em"}},[e("span",{style:{top:"-3.063em","margin-right":"0.05em"}},[e("span",{class:"pstrut",style:{height:"2.7em"}}),e("span",{class:"sizing reset-size6 size3 mtight"},[e("span",{class:"mord mtight"},[e("span",{class:"mord mtight"},"16")])])])])])])])]),e("span",{class:"mspace",style:{"margin-right":"0.2778em"}}),e("span",{class:"mrel"},"="),e("span",{class:"mspace",style:{"margin-right":"0.2778em"}})]),e("span",{class:"base"},[e("span",{class:"strut",style:{height:"0.6444em"}}),e("span",{class:"mord"},"65536")])])]),s(" 个码点。将来根据需要，还可扩展为更多平面。")],-1),u=c('<h3 id="_1-1-bmp、sp" tabindex="-1"><a class="header-anchor" href="#_1-1-bmp、sp" aria-hidden="true">#</a> 1.1 BMP、SP</h3><p>第 0 个平面称为基本多语言平面（BMP, Basic Multilingual Plane），简称为进本平面，平面 0，覆盖当今世界上正在使用中的常用字符。</p><p>我们平常用到的大多数常见字符，一般都是位于 BMP 平面上的。</p><p>BMP 以外的其他平面为增补平面（SP, Supplementary Plane），也称辅助平面，目前共 16 个增补平面 SP。</p><p>增补平面要么用来表示一些非常特殊的字符（比如不常用的象形文字、远古时期的文字等），多半只有专家在历史和科学领域里才会用到它们；要么被留作扩展之用，尚未被分配字符，称之为保留区。目前Unicode字符集的17个平面中尚有大量的保留区编号空间未被使用。</p><p>另外，BMP 平面中有一个私用区，也称为专用区（PUA：Private Use Area，或PUZ：Private Use Zone）：0xE000~0xF8FF（十进制 57344~63743），共6400个码点，被保留为私用，Unicode 官方未将之分配给任何 Unicode 字符，因而可根据需要由合作者之间私下协商将其分配给私有字符。</p><blockquote><p>注：Unicode 标准到目前为止实际上共定义了三个私用区：一个为如上所述的BMP中的 U+E000~U+F8FF，另外两个几乎包含了整个第 15 平面和第 16 平面，分别为 U+F0000~U+FFFFD 和 U+100000~U+10FFFD；私用区相当于是可以由 Unicode 官方之外的个人和机构自由定义字符的特殊区域，因此私用区中的同一个码点，可被分配给不同的字符，具体是哪个字符，取决于用户使用的字体文件，从而导致不同的用户由于安装了不同的字体文件，有可能所看到的私有字符也不同。</p></blockquote><p>BMP 中还有一个被称为代理区（Surrogate Zone）的特殊区域：0xD800-0xDFFF（十进制55296~57343），共 2048 个码点，这些码点被称之为代理码点，目的是用基本平面BMP中的两个码点“代理”表示 BMP 以外的其他增补平面 SP 中的字符。</p><h3 id="_1-2-utf-16" tabindex="-1"><a class="header-anchor" href="#_1-2-utf-16" aria-hidden="true">#</a> 1.2 UTF-16</h3><p>Unicode字符集的字符编码方式一开始规定，使用两个字节的码元（即16位码元）来统一表示所有的字符（即 UTF-16）。UTF-16 中将 ASCII 码的长度由 8 位扩展为 16 位（高 9 位永远是 0），其余的字符全部重新统一编码。</p><p>无论是半角的英文字母还是全角的汉字，它们都表示“1个字符”，其编码也都是“2个字节”。</p><p>Unicode 在制订 UTF-16 字符编码之处，没有考虑与任何一种现有的字符编码保持完全兼容。例如要将文本内容在 UTF-16 和 GBK 之间进行转换，只能通过查表这样低效率的办法。即便是 ASCII 字符，也属于是不完全的间接兼容或半兼容，因为 UTF-16 是两个字节表示的，不同于 ASCII 的单字节编码。于是后来又设计了 UTF-8 字符编码方式，从而实现了与 ASCII 编码的完全兼容。</p><h3 id="_1-3-码点名称" tabindex="-1"><a class="header-anchor" href="#_1-3-码点名称" aria-hidden="true">#</a> 1.3 码点名称</h3>',13),_=e("code",null,"U+",-1),U=e("strong",null,"码点名称",-1),F=e("strong",null,"字符名称",-1),g={href:"https://home.unicode.org/",target:"_blank",rel:"noopener noreferrer"},P=e("blockquote",null,[e("p",null,"Unicode字符集中的 U+0000~U+007F（即十进制的 0~127），跟ASCII字符集（即 ISO/IEC 646 标准）是一致的；U+0000~U+00FF（即十进制的 0~255），跟 ISO/IEC 8859-1 字符集（即Latin-1字符集）也是一致的。")],-1);function x(f,I){const a=o("ExternalLinkIcon");return l(),r("div",null,[e("p",null,[s("本系列文章是对"),e("a",p,[s("知乎专栏「刨根问底字符编码」"),n(a)]),s(" 的学习记录。")]),h,d,m,u,e("p",null,[s("Unicode 字符集为每个字符根据其所在的码点分配了唯一的码点值（即码点编号）。同时为每个码点赋予了一个正式的名称，用 "),_,s(" 后接十六进制数字表示，称为"),U,s("或"),F,s("。（U+0041 表示英文大写字母 A，可在"),e("a",g,[s("这里"),n(a)]),s("查询")]),P])}const B=t(i,[["render",x],["__file","Chapter3.html.vue"]]);export{B as default};
