import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as r,c as i,b as e,d as n,e as s}from"./app-1e7cdb66.js";const c={},_=e("h2",{id:"_1-ai-agent-定义",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-ai-agent-定义","aria-hidden":"true"},"#"),n(" 1. AI Agent 定义")],-1),d=e("p",null,"AI Agent，或称智能代理，是一种基于人工智能技术的软件实体，具备某种程度的自主性和智能性，能够感知其环境、采取决策并执行相应的行动，以实现既定的任务或目标。这些代理通常被设计用来模拟人类智能，但也可以根据任务的需要具备不同程度的自主性。AI Agent 在各种领域都有广泛的应用，包括自动驾驶汽车、机器人、虚拟助手和游戏智能角色。它们的工作原理基于不同的机器学习技术，如深度学习和强化学习，以便学习并改进其决策能力。",-1),h={href:"https://en.wikipedia.org/wiki/Intelligent_agent",target:"_blank",rel:"noopener noreferrer"},l=e("h3",{id:"大语言模型",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#大语言模型","aria-hidden":"true"},"#"),n(" 大语言模型？")],-1),A=e("p",null,"与现在的大语言模型（例如chatGPT）不同，AI Agent 的主要特点在于其能力不仅限于文本生成和自然语言处理。尽管chatGPT等语言模型在文本生成方面取得了显著的进展，但它们更多地是基于自然语言处理技术的被动响应式系统，用于回答问题和生成文本。相比之下，AI Agent 是主动的实体，具备自主决策和行动的能力。它们能够感知和理解更广泛的环境信息，从多个感官源获得数据，然后采取行动以实现任务。例如，自动驾驶汽车是一个AI Agent的示例，它能够感知道路和周围的车辆，做出决策以控制车辆的行驶，而chatGPT等大语言模型则主要用于自然语言理解和生成。",-1),g=e("p",null,"此外，AI Agent的设计和应用通常涉及更广泛的工程和硬件系统，以支持其感知、决策和执行任务。这些系统可能包括传感器、执行器、控制算法等，以确保AI Agent能够在实际世界中操作。大语言模型更多地集中于自然语言理解和生成，通常不包括与物理世界的直接交互。因此，尽管AI Agent和大语言模型都代表了人工智能领域的重要进展，但它们在目标和应用领域上存在显著的差异。",-1),f=e("h2",{id:"_2",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_2","aria-hidden":"true"},"#"),n(" 2.")],-1);function p(I,u){const t=o("ExternalLinkIcon");return r(),i("div",null,[_,d,e("p",null,[n("参考链接："),e("a",h,[n("AI Agent Definition"),s(t)])]),l,A,g,f])}const k=a(c,[["render",p],["__file","index.html.vue"]]);export{k as default};
